operations.schema:
input EditClientInfoInput {
  clientId: PHID!
  name: String
  icon: String
}

input SetRequestForProposalInput {
  rfpId: PHID!
  title: String!
}

input EditInitialProposalInput {
  id: ID!
  sowId: PHID
  paymentTermsId: PHID
  status: ProposalStatusInput
  proposalAuthor: ProposalAuthorInput
}

enum ProposalStatusInput {
  DRAFT
  SUBMITTED
  ACCEPTED
  REJECTED
}

input ProposalAuthorInput {
  id: PHID!
  name: String
  icon: URL
}
